mapper.select:
select id, tel_number as telPhone, real_name as realName, organization, province, city, login_type as loginType, ip_address as ipAddress, invitation_code as invitationCode, head_img_path as headImgPath, date_format(last_login_time, '%Y-%m-%d') as lastLoginTime from c_manager_info where
deleted = 0 and status = 0 and
tel_number = #{tel} and login_password = #{psw};

mapper.update:
update c_manager_info 
set 
    login_password = #{passwordConfirm}
where
    id = #{id};
